INSERT INTO `faq` (`faq_id`, `question`, `answer`, `lang`, `category`) VALUES
(1,	'Mohu po zaregistrování týmu ještě dodatečně měnit informace o členech týmu?',	'Ano, ale pouze do ukončení registrace.',	'cs',	'registration'),
(2,	'Mohu po zaregistrování týmu ještě dodatečně měnit složení týmu?',	'Ano, ale pouze do ukončení registrace.',	'cs',	'registration'),
(3,	'Mohu po zaregistrování týmu ještě dodatečně měnit název týmu?',	'Ano, ale pouze do ukončení registrace.',	'cs',	'registration'),
(4,	'Mohu se soutěže zúčastnit, i když už nejsem žákem střední školy?',	'<p>Ano, pro týmy, jejichž členové jsou absolventi středních škol, je připravena kategorie Open, kde lze soutěžit bez rozdílu věku či vzdělání.</p>\r\n\r\n<p>Pokud jsi však zatím žákem základní školy, můžeš se zúčastnit v kategoriích A,B nebo C. </p>',	'cs',	'registration'),
(5,	'Mohu se soutěže zúčastnit, i když jsem na střední školu (případně odpovídající ročník gymnázia) ještě nenastoupil?',	'Ano. Musíš ale počítat s tím, že nejnižší kategorie, ve které můžeš soutěžit je kategorie středoškoláků v prvním ročníku. Budeš tedy soutěžit ve stejné kategorii jako oni. Více najdeš v pravidlech.',	'cs',	'registration'),
(6,	'Může se zúčastnit kamarád z cizí země?',	'Ano. Může soutěžit ve stejné kategorii, i když nebude součástí týmu z České nebo Slovenské republiky. Samozřejmě velkou výhodou pro něj bude, pokud bude umět anglicky nebo česky, potažmo slovensky.',	'cs',	'registration'),
(7,	'Budeme moci s organizátory soutěže v jejím průběhu nějak komunikovat? Jak? V jakých jazycích?',	'<p>Organizátoři v průběhu celé soutěže oznamují důležité informace týkající se soutěže prostřednictvím elektronické nástěnky, která bude umístěna na našich stránkách.</p>\r\n\r\n<p>Vaše otázky týkající se soutěže zasílejte výhradně na emailovou adresu <a href=\"mailto:%22Fyziklání%20Online%22%3conline@fyziklani.cz%3e\">online@fyziklani.cz</a>. </p>\r\n\r\n<p>Organizátoři jsou schopni komunikovat v českém, slovenském případně anglickém jazyce. </p>\r\n',	'cs',	'game'),
(8,	'Můžu v průběhu soutěže vyhledávat informace pomocí internetu?',	'Ano, ale nesmí to být informace z komunikačních kanálů s lidmi, kteří nejsou na soupisce vašeho týmu.',	'cs',	'game'),
(9,	'Jaké jsou části soutěže a v čem se liší?',	'Soutěž má dvě paralelní části. Hlavní část je započata startem soutěže a skládá se z jedné linie. Hurry-up část začíná hodinu poté, obsahuje 3 souběžné linie. Obě části končí současně a v polovině celé soutěže se zpřístupní možnost přeskakovat úlohy. ',	'cs',	'game'),
(10,	'V jakých jazycích bude psáno zadání úloh?',	'V češtině a angličtině.',	'cs',	'tasks'),
(11,	'Kdy, popřípadě za jakých okolností, budeme dostávat v průběhu soutěže nová zadání úloh?',	'Zadání prvních sedmi úloh dostanete při samotném zahájení soutěže. Další přísun nových úloh přijde se začátkem Hurry-up série. V průběhu soutěže se o zisk dalších nových úloh musíte zasloužit sami. Možné jsou dva způsoby. Buďto získáte novou úlohu díky tomu, že vyřešíte nějakou úlohu předešlou, nebo úlohu tzv. přeskočíte. Vždy se jedná o jednu úlohu, za jednu úlohu. Detailnější informace najdete v pravidlech soutěže.',	'cs',	'tasks'),
(12,	'Jak se vyvíjí obtížnost úloh s pořadovým číslem úlohy?',	'Průměrná obtížnost úloh roste, nicméně i v pozdější fázi soutěže se můžou objevit jednodušší úlohy. Pamatujte také na to, že obtížnost je individuálně vnímaná věc.',	'cs',	'tasks'),
(13,	'Dostaneme autorská řešení úloh?',	'Ano, po ukončení soutěže se autorská řešení objeví na našich stránkách. Nějakou dobu po ukončení soutěže budou ještě organizátoři na stránkách aktivně sledovat soutěžní fórum a reagovat na vaše případné připomínky, dojmy nebo reklamace vztahující se k úlohám nebo soutěži obecně.',	'cs',	'tasks'),
(14,	'Na kolik platných cifer máme náš výsledek úlohy zadávat?',	'V zadání každé úlohy je uvedeno, kolik platných cifer vyžadujeme. ',	'cs',	'submits'),
(15,	'V zadání není specifikováno, v jakých jednotkách máme řešení odesílat. Jak si máme poradit?',	'Pokud je tomu tak, odesílejte vaše řešení v základních jednotkách SI.',	'cs',	'submits'),
(16,	'Chceme odpovědět na úlohu, ale výsledek nelze odeslat. Co se děje?',	'Důvodem zřejmě je, že jste před méně než jednou minutou na nějakou úlohu odpověděli chybně. Pokud tomu tak není, kontaktuje, prosím, organizátory soutěže.',	'cs',	'submits'),
(17,	'Když na úlohu odpovím špatně, mohu svoji odpověď opravit?',	'Ano, na každou úlohu můžete odpovídat opakovaně v neomezovaném množství až do konce soutěže. Mějte ale na paměti, že za každou špatnou odpověď může klesnout možný bodový zisk z této úlohy, a také, že se na 1 minutu po špatném zodpovězení úlohy uzavře možnost odpovídat na libovolnou úlohu dané skupiny (úlohy hlavní části a tři skupiny v Hurry-up). Pro více informací nahlédněte do pravidel soutěže.',	'cs',	'submits'),
(18,	'Když budeme chtít odeslat výsledek jako desetinné číslo, máme pro zápis použít desetinnou čárku nebo tečku?',	'Je jedno, který znak použijete. Nepoužívejte však žádné oddělovače tisíců.',	'cs',	'submits'),
(19,	'Jaké všechny formáty zápisu čísla jsou akceptovány?',	'<p> Akceptujeme tvar čísla popsaný na\r\n<a href=\"http://php.net/manual/en/language.types.float.php\">následujícím odkazu</a>.</p>\r\n\r\n<p>Navíc akceptujeme jak desetinnou čárku, tak desetinnou tečku. </p>',	'cs',	'submits'),
(20,	'Kdy můžu úlohu přeskočit?',	'Úlohu je možno přeskočit v druhé polovině soutěže (od 18:30).',	'cs',	'skipping'),
(21,	'Je za přeskočení úlohy nějaký bodový postih?',	'Ano, za přeskočení úlohy dostanete penalizaci mínus jeden bod do vašeho celkového skóre.',	'cs',	'skipping'),
(22,	'Kolik úloh můžu přeskočit?',	'Celkem můžete přeskočit maximálně deset úloh, a to v hlavní i hurry-up linii dohromady.',	'cs',	'skipping'),
(23,	'Můžeme se k přeskočené úloze ještě vrátit?',	'Ne.',	'cs',	'skipping'),
(24,	'Jak můžeme získat bonusové body?',	'Bonusové body můžete získat za správně vyřešenou sadu úloh v sérii Hurry-up v daném časovém limitu. Více najdete v pravidlech soutěže.',	'cs',	'scoring'),
(25,	'Za úlohu, která byla oceněna X body jsme dostali Y bodů. Jak to?',	'Je to proto, že za špatně zodpovězenou úlohu klesne při následující odpovědi možný bodový zisk z této úlohy. Pro upřesňující údaje ohledně strhávání bodů prosím nahlédněte do pravidel soutěže.',	'cs',	'scoring'),
(26,	'Úlohu jsme vyřešili správně, ale ve výsledkové listině se nám záznam o správně vyřešené úloze neobjevil. Jak to?',	'Výsledková listina se obnovuje průběžně jednou za dvě minuty. Musíte chvíli počkat. Také pamatujte na to, že 20 minut před koncem se výsledková listina přestane obnovovat úplně, abyste byli trochu napjatí.',	'cs',	'scoring'),
(27,	'Co je to Hurry-up?',	'Je to období soutěže v intervalu 18:00 až 20:00, kdy budete mít možnost řešit nové relativně jednoduché úlohy, avšak po 18:30 za ně už nebudete dostávat bonusové body. Jde o paralelně probíhající linii k linii hlavní. Sérii Hurry-up však provází jistá specifika v pravidlech, která najdete na stránce<a href=\"/pravidla\"> s pravidly naší soutěže</a>.',	'cs',	'hurry-up'),
(28,	'Můžu odevzdávat řešení úloh standardní série v období Hurry-up série?',	'Ano.',	'cs',	'hurry-up'),
(29,	'Jak se dozvím, že Hurry-up série už začala?',	'Podle hodin. Bude to zároveň oznámeno na nástěnce. Také by se vám měly objevit nové úlohy k řešení — k tomu však může být potřeba obnovení stránky.',	'cs',	'hurry-up'),
(30,	'Řídí se bodování a strhávání bodů z úloh Hurry-up série stejnými pravidly jako u ostatních úloh?',	'Ano, avšak počet bodů se může snížit až na nulu. Přesnou bodovací tabulku najdete v <a href=\"/pravidla\">pravidlech</a>.',	'cs',	'hurry-up'),
(31,	'Kdy můžu Hurry-up řešit?',	'Od 18:00 do 18:30 lze získávat bonusové body, po 18:30 budou výsledky úloh z Hurry-up linie dále akceptovány, nebudete za ně však dostávat bonusové body.',	'cs',	'hurry-up'),
(32,	'Dostanu diplom nebo nějaké osvědčení o účasti?',	'<p>Certifikáty o účasti zasíláme všem týmům automaticky na email.</p>\r\n\r\n<p>Pokud vám nepřišel, zkontrolujte si prosím složku se spamem, případně se neváhejte ozvat na email  <a href=\"mailto: online@fyziklani.cz\">online@fyziklani.cz</a>. </p>',	'cs',	'otherFAQ'),
(33,	'Pomůže mi výborný výsledek v této soutěži při přijímacích zkouškách na VŠ?',	'Pravděpodobně ne, ale zkusit to můžete.',	'cs',	'otherFAQ'),
(34,	'Na jakou adresu mám psát otázky ohledně soutěže, ještě než soutěž začne?',	'<a href=\'mailto:online@fyziklani.cz\'>online@fyziklani.cz</a>',	'cs',	'otherFAQ'),
(35,	'Kde naleznu všechny důležité informace o soutěži?',	'Na následující <a href=\"/o-soutezi\">stránce</a> a v <a href=\"/pravidla\">pravidlech</a>.',	'cs',	'otherFAQ'),
(36,	'Can I change the information about team members after registration?',	'Yes but only until the end of the registration period.',	'en',	'registration'),
(37,	'Can I change the composition of my team after I create it?',	'Yes but only until the end of the registration period.',	'en',	'registration'),
(38,	'Can I change the name of the team after creating it?',	'Yes but only until the end of the registration period.',	'en',	'registration'),
(39,	'Can I take part even if I am not a high school student?',	'Yes. The Open category is for you. More in the rules.',	'en',	'registration'),
(40,	'Can I take part even though I haven\'t started attending high school or grammar school?',	'Yes. But you must expect that you will be in the same category as the most junior highschool students. You can find more in the rules.',	'en',	'registration'),
(41,	'Can a foreigner take part?',	'Yes. Of course a huge advantage for them would be if they knew English or Czech.',	'en',	'registration'),
(42,	'Is it possible for participants to communicate with the organizers? If so how and in what languages?',	'<p>Organisers will during the competition give out important information through our in-game noticeboard that will be available on our website.</p>\r\n\r\n<p>Send your questions about the tasks and other things to <a href=\"mailto: online@fyziklani.cz\">online@fyziklani.cz</a>. </p>\r\n\r\n<p>Organisers communicate in Czech, Slovak or English only. </p>',	'en',	'game'),
(43,	'Can I use the internet to find information during the competition?',	'Yes but you can\'t communicate with non-members of your team.',	'en',	'game'),
(44,	'What are the parts of the competition and how do they differ?',	'The competition has two parallel parts. The major part starts at the beginning of the competition and one hour later the Hurry-up series will begin. These will then run in parallel until the competition ends. In the second half of the competition you will be able to skip problems. For the Hurry-up series see the rules and the questions and answers below.',	'en',	'game'),
(45,	'In what language will the problems be?',	'In Czech and English.',	'en',	'tasks'),
(46,	'When and how will we get new problems to solve during the competition?',	'You will get the seven initial problems when the competition starts. You can expect new problems with the Hurry-up series. During the competition you must solve the problems you have to get new ones or, in the second part, by skipping problems. The exchange is always one for one. You can find more information in the rules.',	'en',	'tasks'),
(47,	'How does the difficulty of the problem change with its number?',	'The average difficulty rises as you do the problems but there still might be some easier problems later on. Also, remember that difficulty is highly individual.',	'en',	'tasks'),
(48,	'Will we get author\'s solutions?',	'Yes, after the end of the competition the solutions will appear on our website. Some time after the end the organizers will still be on the website and will be reacting to your recommendations, experiences or troubles relating the competition or the problems.',	'en',	'tasks'),
(49,	'How many significant digits should one give in the solution?',	'Each task has specified how many significant digits the result should have.',	'en',	'submits'),
(50,	'The task doesn\'t tell us which units to submit the result in. What should we do?',	'If it is so then just submit it in the base SI units.',	'en',	'submits'),
(51,	'We want to submit the answer to the problem but we can\'t. What is going on?',	'The reason must be that you submitted a wrong solution to a task less than a minute ago. If it isn\'t so then please contact the organizers.',	'en',	'submits'),
(52,	'If I submit the wrong answer can I correct it?',	'Yes, you can answer any question as many times as you want to. Remember though that by submitting wrong answers you will lower the potential point gain from the question and also that you can\'t answer the problems from the same category incorrectly more than once in a minute (the problems from the main category and the three hurry-up categories). For more information see the rules of the competition.',	'en',	'submits'),
(53,	'If we want to submit the solution as a decimal should we use a decimal point or comma?',	'The symbol is irrelevant but don\'t use anything to denote thousands (i. e., use 1000000 instead of 1,000,000) .',	'en',	'submits'),
(54,	'What are the accepted formats of numbers?',	'<p>We accept the formats described on\r\n<a href=\"http://php.net/manual/en/language.types.float.php\">following website</a>.</p>\r\n<p>We enable the usage of both the comma and the decimal point.</p>\r\n\r\n\r\n\r\n',	'en',	'submits'),
(55,	'When can I skip a problem?',	'You can skip problems in the second part of the competition (from 18:30 CET).',	'en',	'skipping'),
(56,	'Will I lose points for skipping problems?',	'Yes, you will lose one point per skipped problem from your total score.',	'en',	'skipping'),
(57,	'How many problems can I skip?',	'You can skip 10 problems in total (major part and hurry-up series altogether).',	'en',	'skipping'),
(58,	'Can I come back to a problem I skipped?',	'No.',	'en',	'skipping'),
(59,	'How do we get bonus points?',	'You can get bonus points for solving a whole collection of Hurry-up problems in the time limit. More about this in the rules.',	'en',	'scoring'),
(60,	'We got Y points for a problem that was supposed to be worth X points. What happened?',	'This is caused by the fact that wrong solutions decrease the point gain from that problem. To get more information about this look at the rules.',	'en',	'scoring'),
(61,	'We solved the problem but this hasn\'t appeared on the scoreboard yet. How come?',	'The scoreboard refreshes every two minutes so wait a bit longer. Also remember that twenty minutes before the end of the competition the scoreboard stops refreshing completely to add extra tension.',	'en',	'scoring'),
(62,	'What is Hurry-up?',	'It is a period in the competition between 18:00 and 20:00 CET when you can solve new and relatively easy problems. However, you can get bonus points only until 18:30 CET. It is a series of problems that runs in parallel to the major competition. The Hurry-up series has some specific rules though. You can find more about this in <a href=\"/rules\">the rule section</a>.\r\n',	'en',	'hurry-up'),
(63,	'Can I submit solutions to the normal problems during this part of the competition?',	'Yes.',	'en',	'hurry-up'),
(64,	'How will I find out that the Hurry-up series has begun?',	'Using a clock. It will also be announced on the noticeboard. You should also have new problems to solve — but to see that refreshing the page will be necessary.',	'en',	'hurry-up'),
(65,	'Is the awarding of points during the Hurry-up series governed by the same rules as the standard part of the competition?',	'Yes, but you can also get zero points. You can find the exact scoring table in <a href=\"/rules\">the rules</a>.',	'en',	'hurry-up'),
(66,	'When will I be enabled to solve Hurry-up problems?',	'You can get bonus points only in a period between 18:00 and 18:30 CET, after that we will be accepting solutions to the Hurry-up problems but you will not get the bonus points.',	'en',	'hurry-up'),
(67,	'Will I get a certificate for attending?',	'<p>We will send certificate for attending (with score) to all teams via email.</p>\r\n\r\n<p>In the case you didn\'t receive it, please check your spam folder. In case that it is not in spam, contact us on  <a href=\"mailto: online@fyziklani.cz\">online@fyziklani.cz</a>. </p>',	'en',	'otherFAQ'),
(68,	'Will my result in this competition help me with an application to a university?',	'Probably not but you can try to use it.',	'en',	'otherFAQ'),
(69,	'Where should I send my questions about the competition before it starts?',	'<a href=\'mailto:online@fyziklani.cz\'>online@fyziklani.cz</a>',	'en',	'otherFAQ'),
(70,	'Where can I find all the important information about the competition?',	'On following <a href=\"/about\">website</a> and in the\r\n<a\r\nhref=\"/rules\">rules</a>.',	'en',	'otherFAQ');
INSERT INTO `report` (`report_id`, `event_id`, `text`, `lang`) VALUES
(1,	132,	'<p>Our review of the Online Physics Brawl is only positive. It was a great opportunity to try solving problems that are very different from the ones you can find in textbooks or competitions like the Physics Olympiad. It also enabled us to compare our problem solving skills with other students.</p>\r\n<p>Physics Brawl was a great way to spend a late afternoon in the company of amazing people and physics. Although the members of our team are scattered all over the country, it paid off that we met at one place since we had a great time hanging out together, and it helped to make the communication more effective.</p>\r\n<p>The organization of the competition was perfect. There were only a few technical problems that were swiftly taken care of. Also we appreciated the addition of the Hurry-up series that is not normally present in the Physics Brawl. </p>\r\n<p>Although the classic Physics Brawl is better in every way, this online version was very enjoyable as well. We would definitely recommened to participate. It was a great experience, and we are looking forward to competing next year!</p>',	'en'),
(2,	94,	'I am now attaching a photo of our team (Quantum, from Brazil). The small plank I\'m holding is our team mascot, called Planck.<br>\r\n<br>\r\nOur team is participating in the Online Physics Brawl for the second time (last year, we were named Quantum Brazil) in a row. We don\'t necessarily participate expecting to win, since the competition for this olympiad is big, but we love the format of it. The questions are quite challenging (IPhO training-level, in my opinion), and along with the time constraint and the quick assistance done in real time by you guys (along with thorough, immediate results at the end) just build up on an amazing olympic experience.<br>\r\n<br>\r\nI take care of a website that\'s specialized in talking about scientific olympiads in Brazil, called Olimpíadas Científicas (www.olimpiadascientificas.com), and I was able to see a lot of different online olympiads come and go. You guys are, by far, the best online olympiad I\'ve ever seen.<br>\r\n<br>\r\nI hope my friends and I participate in the Online Physics Brawl next year as well.<br>\r\n<br>\r\nThanks for the experience.',	'en'),
(8,	94,	'We are from Colombia and really enjoyed the contest!<br>\r\n<br>\r\nThank you for the opportunity!',	'en'),
(9,	94,	'Tak sice asi nevyhrajeme, ale posíláme alespoň fotku nás všech z týmu GYMPL :D<br>\r\n<br>\r\nNázev fotky je \"Nejlepší spolupracovníci\".',	'cs'),
(10,	94,	'Fyziklání bylo velmi náročné, ale úlohy jsme řešily s úsměvem. Často jsme si rvaly vlasy, když nám došlo, že vše co jsme vypočítaly, bylo marné, ale chvíle úlevy po dobře vypočítaném příkladu nám zase dodaly naději. Celá soutěž nás stmelila a poučila. Rády se zúčastníme i příští rok.',	'cs'),
(11,	94,	'Fyziklání online sme riešili priamo v priestoroch našej školy (GAMČA). Radi by sme poďakovali škole za túto možnosť.<br>\r\n<br>\r\nV priebehu súťaže u nás na škole bola príjemná atmosféra. Občas nastalo napätie pred odovzdávaním príkladu, či je správne vyrátaný. Súťaž sme si užili a sme radi, že sme sa do nej mohli zapojiť.​ Na fotografii sú zľava: Tomáš Sásik, Matej Parada, Barbara Pulmannová, Nina Raganová a Sára Kuťková.',	'cs'),
(12,	94,	'Posíláme 3 fotky našeho týmu před a po řešení Fyziklání Online. Sešli jsme se na Fakultě matematiky na University of Cambridge. Dobrá soutěž - díky!<br>\r\n<br>\r\nNáš tým (na fotkách zprava): Stanislav Fořt, Jakub Vošmera, Dominik Miketa, Michal Buráň',	'cs'),
(13,	94,	'',	'cs'),
(14,	94,	'Ve středu 2. 12. 2015 se v odpoledních hodinách na pražském Gymnáziu Špitálská sešli tři mladí studenti  Jakub Janoušek, Michal Koldinský a Eliška Synková, aby společně řešili úlohy letošního ročníku Fyziklání online. Od svých obrazovek z pohodlí domova jim pomáhal Vít Janda a Tomáš Faikl. Je nutné podotknout, že každý ze zmiňovaných navštěvuje jinou školu a to i v různých městech. Naše pětice se poznala letos během soutěže Expedice Mars, o čem svědčí také náš název Expedice Mars 2015. V 17:00 soutěž započala a na webových stránkách soutěže se začaly objevovat první úlohy. Naší pětici šlo z počátku vše rychle od ruky, s dalšími příklady však přicházela vyšší obtížnost. Konečný bodový zisk našeho týmu činil v době ukončení soutěže ve 20:00 57 bodů, což odpovídá 12 vyřešeným úlohám. Tento bodový zisk nám zajistil 7. pozici v celkovém pořadí kategorie C. Soutěže jsme se letos zúčastnili poprvé, v příštím roce už plánujeme dosáhnout na nejvyšší příčky.<br>\r\n<br>\r\nNa fotografii zleva: Michal Koldinský, Jakub Janoušek, Eliška Synková',	'cs'),
(15,	94,	'Mega bolo,<br>\r\n<br>\r\ndobre sa nam ratalo - v skole a jeden cez net. Priklady boli dost dobre, pomenej mechaniky potesilo. Pre mna top moment vyriesenie 32 a zistenie, ze E/B=c pre elmag vlnu. Na odoslanie posledneho prikladu nam 15 sekund nezvysilo :P',	'cs'),
(16,	94,	'Psala se středa 2. listopadu 2015. Nad Třincem se setmělo, lidé již dojeli z práce a z nákupů domů, děti ze škol rovněž. V dáli září železárny a hle, na gymnáziu se ještě také svítí. Proč to?<br>\r\n<br>\r\nJak se již stalo tradicí, v prvních prosincových dnech se tu dobrovolně v 17 hodin večer sejde parta, kterou by většina spolužáků označila za blázny, jež obětují tři hodiny volného času ve prospěch fyziky. Pro nás se však Fyziklání resp. Fyziklání online stalo v průběhu let srdcovou záležitostí. Před čtyřmi lety se na „gymplu“ začal formovat tým, který neměl ty nejvyšší ambice, nebyl složen z pěti Einsteinů a nevěděl, co čekat. Co nám však nechybělo, bylo odhodlání, spousta jídla a sladkostí a hlavně týmový duch. Pětice studentů, která doznala drobných změn a skončila ve finálním složení  Lukáš, Štěpán, Jirka, Kamil a Maku však zanedlouho začala dobývat svět. Během následujících let jsme se Fyziklání a Fyziklání online zúčastnili pokaždé, řešili je s velkým nadšením a měli ohromnou radost z našich úspěchů, ale hlavně, měli jsme radost ze hry. Klasické Fyziklání pro nás vždy znamenalo krásný výlet do dalekého hlavního města s paní profesorkou, dále možnost se lecčemu přiučit, zavařit si hlavu nad zajímavými úlohami a v dobré náladě usedat odpoledne do vlaku směr dálný východ. Fyziklání online sice postrádá efekt přímého soupeření s ostatními v jedné místnosti, avšak na půvabu rozhodně neztrácí.<br>\r\n<br>\r\nTento rok jsme se tak rozhodli do něj znova s chutí pustit, tentokrát poprvé v nejstarší kategorii A. Jídla a pití bylo dost, technika zlobila, knih, tabulek a papírů bylo dost a hodiny ukazovaly 16:59, zkrátka všechno jak má být. Jako vždy zdravě nahecovaní a napjatí jsme čekali zadání prvních úloh, které v zápětí přišlo. Následující tři hodiny se pak asi dají nejlépe popsat jako změť počtů, náhod, nových myšlenkových postupů, ťukání do kalkulačky, hledání v tabulkách, stěžování si na banální chyby, záblesků radosti ze správných výsledků a klasického shonu těsně před koncem. To vše však ve veselé a pohodové atmosféře našeho tradičního doupěte  studovny s pohodlnými gauči. Náš tradiční rituál nedívání se na výsledky byl bohužel asi dvě minuty před koncem zhacen naší paní profesorkou. Nic naplat, soutěž je ukončena, máme toho dost a vidíme naše umístění  6. místo. Vzhledem k tomu, že jsme poprvé soutěžili v kategorii A, a né všichni plánujeme kariéru v CERNu, jsme byli s výsledkem spokojeni. Krátce jsme zkontrolovali naše mladší kolegy a potencionální nástupce, kteří soutěžili v kategorii C a nostalgicky zavzpomínali na mladá léta. Čas hrozně letí, když jste maturant. Po dlouhém dni v „ústavu“ už ale hurá domů, s myšlenkami upřenými k tomu, jak se v Praze co nejlépe rozloučit s epochou jménem Fyziklání.<br>\r\n<br>\r\nNezbývá, nežli znovu poděkovat organizátorům za skvěle připravenou soutěž, kterou považujeme za tu nejlepší pod sluncem. Těšíme se na Vás v Praze!<br>\r\n<br>\r\nTým Gymtri (Markéta Kubiková, Lukáš Supik, Štěpán Stenchlák, Jiří Mravec, Kamil Cinkraut)',	'cs'),
(17,	94,	'Ahojte,<br>\r\n<br>\r\nnejdřív bych chtěla říct, že fyziklání bylo super a zároveň nemám absolutně tušení, co mám napsat do téhle zprávy. Soutěžili jsme v počítačové učebně na Přírodovědecké fakultě UP ješte s jedním týmem z naší školy, který byl ale v nižší kategorii. Nejdřív nám šlo počítání jako po másle, ale pak jsme se postupně začali zadrhávat. Já jsem například vždycky došla buď k nějakému naprosto nesmyslnému obřímu číslu, nebo šílenému vzorečku. Honza, který seděl vedle mne poslední hodinu už jen bezradně zíral do monitoru a občas přepínal zadání úloh. Kluci za náma zase počítali stylem: ...hmm, nevychází nám to, tak tohle zanedbáme a tam něco přičteme a hele, máme výsledek. :D<br>\r\n<br>\r\nNakonec jsme skončili 12. a tým co tam byl s námi, ale v nižší kategorii nás dokonce i bodově předběhl.<br>\r\n<br>\r\nCo se týče fotky, tak kluci se nechtěli fotit a jelikož já nemám mobil s foťákem, tak jsem to fotila cizím. Podle toho to taky vypadá.',	'cs'),
(18,	94,	'V útulnej FKS mietnosti na Bratislavskom MatFyze, s chladničkou plnou diferenciálnych operátorov a pomarančami na riešenie úloh o symetriách zafarbených sfér. A v nemenej útulnej hotelovej izbe v Kórei sa zúčastnil FtáKopySk po tretí krát Fyzikláni Online.<br>\r\n<br>\r\nTento rok s veľmi jasným cieľom: poraziť Smoluchowski\'s team.<br>\r\n<br>\r\nTen tožiž skončil tesne pred FtáKopySkom už predchádzajúce dva ročníky. Motivácia bola zvýšena o stávku, o kamión čohosi o čom neradno hovoriť. Vedľajším cieľom bolo samozrejme poraziť všetkých ostatných a zarátať si zaujímavé úlohy.<br>\r\n<br>\r\nVšetkých ostatných sme porazili, zaujímavé úlohy zarátali. A Smoluchowski\'s team to ešte budúci rok uvidí!<br>\r\n<br>\r\nĎakujeme orgom za super súťaž!<br>\r\n<br>\r\nFtáKopySk ',	'cs'),
(19,	94,	'',	'cs'),
(20,	94,	'Soutěž jsme si jako každoročně velmi užili. Fyziklání nám nejen napomáhá se zdokonalovat ve fyzikálních úlohách, ale taky nás nutí vymýšlet taktiku, abychom byli co nejpřesnější a nejrychlejší. No a jak si takovéto 3 hodinky zpříjemnit? Jedině s dobrým kolektivem, jídlem a kupou zábavy <br>\r\n<br>\r\nPřikládáme fotku jako důkaz, s komentářem: ,,Co není v hlavě, musí být v nohách\'\'.',	'cs'),
(21,	122,	'Ahojte, takto sa počítalo u nás.<br>\r\n<br>\r\nSíce to bolo pre nás, ako prvakou trošku ťažšie úlohy boli naozaj zaujímavé. A už sa tešíme na ďalšie súťaže.<br>\r\n<br>\r\nZdravia Radka, Martin, Matúš a Jozef z Bratislavy.',	'cs'),
(22,	122,	'Letošní Fyziklání online bylo super, poprvé s trošku obnoveným a vyměněným týmem jsme vyrazili v přátelském duchu zkusit poměřit své síly a schopnosti ve fyzice. Protože jsme seděli u monitorů počítačů, nikdo z nás se nebál, že se mu někdo vysměje, když to udělá špatně :-D Také jsme trochu obměnili letošní název, který nevědomky vytvořila naše paní učitelka při hodině ZSV a filosofie. \"Já bytí absolutno\" je opravdu nezapomenutelné :-D<br>\r\n<br>\r\nZe začátku nám šlo počítání úloh poměrně rychle, protože ty startovní jsou (alespoň podle mě) vždy lehčí. Postupem času jsme zpomalovali, až téměř nikdo z nás nic nepočítal, koukali jsme na papíry a nikdo z nás nic netušil. Poté se vrátil náš fyzikář s několika čokoládami a kouskem pizzy pro každého. Po menším občerstvení s pár jouly navíc jsme se pustili do dalších příkladů. Jídlo dělá člověka!<br>\r\n<br>\r\nTěsně před koncem nám bylo oznámeno, že jsme ve škole vlastně zamčeni, a když vylezeme na chodbu, začne vše pípat. Takže plán zněl: Ihned po skončení pomalu vypneme všechny počítače a výtahem vyjedeme do přízemí, kde výtah otevírá dveře přímo ven.<br>\r\nKdyž jsme s mírným zklamáním zjistili, že jsme v naší kategorii byli čtvrtí, bylo načase skončit. Všichni jsme vypnuli počítače a společně s fyzikářem nalezli do malého výtahu. Někdo však náhodou zmáčkl špatné patro, kde se dveře otevřeli směrem do zakódovaného prostoru. Všichni mlčeli a jen náš fyzikář pronesl: \"Nikdo ani hnout.\" :-D Poté jsme již opustili školu a všechno dobře dopadlo.<br>\r\n<br>\r\nKdyž jsme odcházeli, zjistili jsme z telefonátu jednoho příbuzného, že po půlce Příbrami vypadl proud. Bohudík se to nestalo zrovna nám, asi jsme přece jen měli trochu štěstí. \"Bramborové\" čtvrté místo jsme zapili Kofolou a rozjeli se domů.<br>\r\n<br>\r\nJiž teď se těšíme na příští Fyziklání v příštím roce v Praze a také za rok na další Fyziklání online! :-)',	'cs'),
(23,	122,	'Po dlouhém dni ve škole se pět „znuděných“ studentů rozhodlo, že své drahocenné tři hodiny obětují fyzice. Pátá hodina odbila a škola ještě svítila a my už seděli v počítačové učebně vybavení na dlouhé hodiny přemýšlení. Hned co jsme si přečetli první úlohu, dlouhá chvíle ticha, počítání a hlavně filozofování započala, až jsme nakonec zadali špatnou odpověď. Každý ale děláme chyby a my se také poučili a nakonec se občas ozvaly radostné výkřiky ze správných odpovědí. V poslední půlhodině přišel nával adrenalinu a všichni jsme počítali jak o život. Den jsme zakončili využitím prázdných školních prostor pro trošku zábavy ve formě klouzání po podlaze, tuto příležitost totiž nemáme každý den.<br>\r\n<br>\r\nStejně jako nemáme každý den příležitost, strávit čas děláním toho, co nás baví, s lidmi které máme rádi.',	'cs'),
(24,	122,	'We didn\'t know what Physics Olympiad is when we were in primary school. Our first encounter with Olympiads were Online Physics Brawl - we were surprised when we learnt that there was a competition like this. Ever since, we were looking forward to participate in this brilliant competition due to the lack of \'scientific improvement\' opportunities in Turkey. Therefore, we will work the exam problems as hard as we can because we see this examination as a means to express our gratitude to the organizers of this benefical online project. And, we hope that this activity will thrive through the course of years and give hope and passion for scientific advancements in international high school communities.',	'en'),
(25,	122,	'We have participated for the first time. It should be noted that we are the second university from Bangladesh. Truth be told we enjoyed no matter what we have gain or lost. We are concern about Physics Brawl. We will participate upcoming and of course we will inform our friends.',	'en'),
(26,	122,	'We are just some high school students from India, and we never knew such a thrilling competition existed.<br>\r\n<br>\r\nThis was the first time that we appeared for the Online Physics Brawl. It was an amazing experience as the problems were very new and challenging which is often not the case with traditional exams. We hope to participate in the years to follow as well.',	'en'),
(27,	134,	'Šialené fotky sa robia len počas šialených udalostí! Fyzikláni rozhodne takouto udalosťou je. Vlny eufórie za správne vyriešený príklad striedalo počítanie elektrmagnetických vĺn. Ešteže sme sa nedostali ku gravitačným vlnám, ak tam vôbec nejaké boli :) Každopádne super udalosť, ľutujeme že je to ešte len náš prvý ročník. Tešíme sa o rok!',	'cs'),
(28,	134,	'”Kluci, jak se dělá kafe?“ prohlásil chvilku po srazu jediný dívčí člen smečky. Na začátek nic moc, no nevadí... Minutu před startem vyfotit selfie a jde se na to! Dereme se jednou úlohou za druhou, ale jak to tak bývá, narazili jsme... Nejde to? Nevadí, máme Excel. Vůbec to nejde? Vždy je tu C#! Mozek je hladový, a tak samozřejmě nesmí chybět občerstvení. Řešení je teprve potřeba vymyslet, tak proč nezačít krájet papriku? A heuréka, řešení je na světě! (”To jsi na to přišla takhle z krájení papriky?“) Ne vždy je však týmová kapacita na všechno, tak bývá nutné improvizovat. A u kvantové mechaniky jak jinak, než selským rozumem? Konec se blíží, t − 5 min a my už jsme zoufalí. Kafe! Slayer! (Den 36. - stále vidím zvuky...) V osm hodin poslední refresh a následuje úleva: ”Nevyřešené úlohy: 0!“',	'cs'),
(29,	134,	'Sme totálne zúfalí. Nepomáha nám ani jedlo a ani úžasná fotka ktorá vždy zabrala. A čo je najhoršie nepomáhajú ani dve náboj tričká. Ale čo je pozitívne každý už vypočítal príklad takže nie sme až tak spomalený ako sme mysleli. Ale stále sme spomalený natoľko že už hodinu sme nič nevypočítali. Jediné pozitívum je že nám ešte ostala pizza.',	'cs'),
(30,	134,	'We participated for the first time in the Physics Brawl this year and definitely was a very different experience which provided us many learnings due to the distinctive aspects of the competition that are not common in Brazil: a theoretical team tournament with such detailed rules that demands speed, strategy and teamwork. Where we live, there is only a national theoretical olympiad available, so certainly it was a great way to motivate us to keep studying and preparing for other exams, by doing such interesting and varied problems from this year’s contest and latest ones. Even though it is the first time we heard about something like this, we are certainly looking forward to participating in the upcoming editions and share to friends how engaging the Online Physics Brawl is. We appreciate a lot everyone managing this event for giving students opportunities and stimulating a worldwide love for science.',	'en'),
(31,	134,	'I have participated on the Online Physics Brawl since I was a freshman.  Every time, I enjoyed it even more. As I advanced through high school, I acquired more and more knowledge of physics, beginning my lifelong fascination with the subject.  It exhilarates me to work on challenging physics problems for hours! In addition, I was delighted to have worked in a group, since I collaborated with others who shared my passion for physics.  Every year I participated, my team achieved higher and higher rankings, and this year, we placed second in our division, and first in our country! This year\'s results were truly a reward for four years of dedication. Competing in the Online Physics Brawl was a worthwhile experience, and I wish to thank the competition organizers for providing me this opportunity.\r\n',	'en'),
(32,	134,	'We\'re high school 12th years from the Northeast USA (New Jersey). Our team name, DANIELQ CHENG, is named after our friend and a legend of physics at our school, and we joined FYKOS this year in the hopes of emulating his physics prowess. We\'re proud of what we were able to accomplish, and all of our team members noted that it was a very enlightening experience and we\'d never enjoyed doing physics problems as much as we did in FYKOS. This competition helped us realize that so much of the time we learn science concepts such as physics in the classroom just to take tests, but applying them and reasoning our way through problems in the Physics Brawl was an experience we would never forget. The competition sparked a new love for physics in all our team members, and we hope we can compete again in the future!',	'en'),
(33,	141,	'Posíláme fotku zoufalí při posledních pokusech řešení úloh kterým nerozumíme a doufáme aspoň v trička za fotku  :-)<br>\r\n<br>\r\nJinak super soutěž, kde se spoustu nového dozvíme.<br>\r\n<br>\r\nTým Dašák',	'cs'),
(34,	141,	'Opět po roce se náš tým neohrožených žen rozhodl zúčastnit fyziklání. Jelikož jsme už minulý rok soutěžily, věděly jsme co čekat. Naladily jsme se tak na vánoční vlnu a fyziklání rozjely v červeno-bílé sestavě. Připravovaly jsme se dlouhé týdny. Napětí mezi námi by se dalo krájet gama nožem. Po několika příkladech, které jsme vyřešily, jsme na sebe byly opravdu pyšné. V průběhu soutěže jsme si užily spoustu legrace. Podle  nás je totiž nejdůležitější spojení zábavy a fyziky. I podle učitelů jsme byly po oba roky nejveselejší tým, protože po každé vyřešené úloze jsme doslova vyskakovaly až ke stropu. Sice jsme neskončily mezi prvními, ale s naším umístěním jsme velmi spokojené. Už teď se těšíme na další rok s mottem Žijeme si svůj fyziko-příběh. <br>\r\n<br>\r\nS pozdravem tým K-c.',	'cs'),
(35,	141,	'Na Fyziklání v Prahe sme boli tento rok vo februári a už druhý rok po sebe sme sa zapojili do tejto skvelej online sútaže a opäť sa nám to veľmi páčilo!! Hoci nie sme až tak zdatní vo fyzike ako väčšina tímov, ktoré sa pravidelne zúčastňujú, zatiaľ sme si zakaždým riešenie príkladov mimoriadne užívali a mali sme pri nich veľkú srandu :D. Príklady sú naozaj všetky vždy veľmi zaujímavé a ich riešenie nám veľa dalo a donútilo nás rozmýšľať úplne inými spôsobmi. Dúfame, že sa budeme môcť tento rok znova zúčastniť aj normálneho Fyziklání, už sa na to moc tešíme! Ďakujeme Vám za túto výnimočnú súťaž :).',	'cs'),
(36,	141,	'Když s návrhem o účasti ve Fyziklání online přišel náš „kapitán“, nikomu z nás se do soutěžení moc nechtělo. Díky jeho bezbřehé trpělivosti se mu však pět lidí do týmu podařilo sehnat, i když to bylo, jak se říká „za pět dvanáct“. Protože některé členy týmu navzájem dělila vzdálenost cca. 200 kilometrů, padla volba na krásný fyzikální Skype hovor.<br>\r\n<br>\r\nZačali jsme se scházet deset minut před začátkem a v plné síle jsme se sešli asi až po dvaceti minutách soutěže. Začátek byl sice hodně divoký, plný zmaru a zmatku, ale očekávali jsme, že se to časem zlepší. Nezlepšilo. A tak čas utíkal a úlohy mizely jen pomalu.<br>\r\n<br>\r\nPo třech hodinách nastalo vysvobození z naší fyzikální bitvy, kde jsme se pokoušeli porazit sami sebe. Žádné zázračné umístění z toho nevzešlo, ale zážitek, jak zapínáme Minecraft, jen abychom zjistili, jak se rozlévá láva, je k nezaplacení.<br>\r\n<br>\r\nZ Těšína zdraví Ondra, Adam, David, Kristýna a Anička',	'cs'),
(37,	141,	'Všetci sme sa spolu zišli osláviť narodeniny jedného z nás, a to tým, čo máme najradšej - samozrejme, že fyzikou. Na prvý problém sme narazili ešte pred začiatkom súťaže, keďže sme nevedeli zohnať miesto, kde by sme sa všetci spoločne stretli. Ako je možné vidieť, skončili sme v ambulancii. Posilou pri počítaní príkladov bola pizza, ktorá bola štedro na účet nášho oslávenca. Riešenie týchto jedinečných úloh sme si nesmierne užili. Obzvlášť oceňujeme vtipné názvy úloh. Srdečne ďakujeme organizátorom za snahu, ktorú vložili do tejto súťaže, aby sme my mohli stráviť večer takýmto skvelým spôsobom. Spoločné počítanie viac či menej náročných úloh z fyziky nás ešte viac združilo a prinieslo nám úsmev na pery. Dúfame, že sa nám podarí zúčastniť sa aj Fyziklání v Prahe a už sa tešíme na ďalšie budúcoročné Fyziklání online.',	'cs'),
(38,	141,	'Dne 27. listopadu 2019 jsme měli možnost zúčastnit se fyzikální soutěže Fyziklání Online. Z naší školy se do soutěže zapojilo celkem 8 týmů. Tým „Bioložka a čtyři programátoři“ obsadil v mezinárodní konkurenci 17. místo a v rámci ČR vybojovali krásné 4. místo. Další týmy byly méně úspěšné, ale paním učitelkám Věře Krůsové a Jitce Romové, díky kterým jsme se soutěže mohli zúčastnit, jsme přesto radost udělali už jen tím, že jsme svůj volný večer strávili s fyzikou. <br>\r\n<br>\r\nNáš tým, Mikulášská skvadra, se této soutěže zúčastnil vůbec poprvé. Minulý rok jsme se Šimonem (dalším členem týmu) reprezentovali naši školu na Fyziklání, tam se nám ale bohužel moc nevedlo. Já jsem se však zúčastnila ještě soutěží Fyzikální náboj a Matematický náboj, které jsme s naším někdejším týmem ovládli. Tento rok musel zbytek týmu soutěžit ve vyšší kategorii, tak jsem sestavila tým nový. Na Fyzikálním náboji jsme slyšeli o Fyziklání Online a řekli jsme si, že bychom ho rádi zkusili. Takhle začal náš příběh o Fyziklání Online. <br>\r\n<br>\r\nVe škole máme naštěstí dost učeben s počítači, takže si každý tým mohl zabrat svou vlastní učebnu. My jsme si vyprosili učebnu matematiky v půdní vestavbě. Nastartovali jsme počítače, přichystali fixy a pak jen čekali na zadání. <br>\r\n<br>\r\nZe začátku každý řešil svůj příklad a jen zřídka jsme si do toho mluvili. V průběhu soutěže jsme však došli k závěru, že se nám týmová práce vyplatí. Každý znal nějaký střípek a po jejich seskládání jsme se většinou dobrali konce. Někdy jsme bohužel udělali drobné chyby, které nás oddělily od správného výsledku. I tak jsme si ale soutěž užili a získali jsme spoustu nových zkušeností. <br>\r\n<br>\r\nZa tým Mikulášská skvadra, Klára Hubínková',	'cs'),
(39,	141,	'At first, our team, 0x2a, came into this tournament feeling that it would only be about completing problems individually. Yet over the course of the three-hour long competition, we saw just how important it was to collaborate with each other. Setting up an array of tables together, with each of us passing our notebooks back and forth to check answers, we felt that this competition encompassed what a true, real-life scenario may be like in the field of science. At the same time, we enjoyed going through the process of solving and checking some of the most difficult problems. Ordering some to-go Chipotle food and cupcakes to keep our energy going, this was certainly a competition to remember from combining hard-work with a hint of fun. Based on our experiences this year (our first time competing!), we will certainly be coming back next year as well!',	'en'),
(40,	141,	'',	'en'),
(41,	141,	'',	'cs'),
(42,	141,	'Our team consists of five Vietnamese: two high school students and three students having graduated from high school for one or two years.<br>\r\n<br>\r\nIn our country, there is only one annual Olympiad for individual students. Therefore, Online Physics Brawl provided us with a totally fresh experience: a chance for us to cooperate in solving a wide range of problems in a relatively short amount of time. Additionally, the questions were challenging, demanding both advanced physics reasoning and rigorous computational abilities.<br>\r\n<br>\r\nAlthough we finished with just one point fewer than the overall winners and missed the reward, we still received invaluable experiences in three hours of effective teamwork. For three graduated participants, this was truly a ‘game’ of entertaining problems; for others, this was a chance to sharpen their skills and knowledge for the upcoming Vietnam Physics Olympiad.<br>\r\n<br>\r\nFinally, we would like to express gratitude toward the organizers for creating such an interesting competition. Certainly, we will return next year and spread this competition to enthusiastic physics students in Vietnam.<br>\r\n<br>\r\nOur team photo is a screenshot of our Skype conversation. We come from different provinces, so we could not gather offline and had to utilize online communications. However, our passion for physics connected us tightly and fostered extensive collaboration.',	'en'),
(43,	141,	'It was the third year of the team SCI-TECH\'s appearance in the Online Physics Brawl. The line up of the team is generally students from juniors and seniors of our school. Considering that all of the members from the first year\'s team are graduated, it was such an honor to represent our alumni, our school, and our country. Not only Online Physics Brawl is now a tradition for our school, but also competing as a member of SCI-TECH is a legacy of our alumni, which makes us proud. But why are we all eager to participate in this competition?<br>\r\n<br>\r\nFirst, we are studying physics olympiads, and by this competition, we have a chance to compare our performance to other students from all around the world. Secondly, and more importantly, despite the fact that most of the olympiad competitions are individual, Online Physics Brawl is a team-oriented competition. It instills working as a well-coordinated team. Moreover, the contest\'s dynamic structure and the sense of competing are an entertaining aspect that helps differ Online Physics Brawl from its similars since you can see the scores of the other teams in real-time.<br>\r\n<br>\r\nAll in all, Online Physics Brawl is a very different contest and it means a lot for SCI-TECH. For this unique experience, we appreciate the whole organization team and hope to see similar events organized in the future.',	'en'),
(44,	150,	'Středa, 25. listopad 2020. Pro mnoho lidí jen další, již čtvrtá, listopadová středa. Pravda, že by byla tato středa obyčejná se nedá říct  žáci nechodí do školy, obchody jsou zavřené, v ulicích prázdno. Přesto je však tato středa ještě něčím výjimečná. Kromě výročí 28 let od odhlasování rozdělení Československa a Mezinárodního dne bez nákupů tu bylo ještě něco. Něco, co zajímalo především studenty nadšené do fyziky z celého světa  Fyziklání online. Tříhodinová týmová soutěž v řešení zapeklitých fyzikálních úloh organizovaná studenty MFF UK.<br>\r\n<br>\r\nNebylo myslitelné, abychom se této již tradiční fyzikální soutěže nezúčastnili, zvlášť v době, kdy je většina podobných soutěží rušena. To však Fyziklání online nehrozí, protože již od počátku probíhá online. S předstihem jsme tedy sestavili pětičlenný tým, který jsme otestovali na Fyzikálním náboji.<br>\r\n<br>\r\nSamotné Fyziklání začalo ve středu v 17:00, odkdy se také odpočítával tříhodinový limit. My jsme se však spojili s dostatečným předstihem, abychom si důkladně osvojili pravidla a naplánovali taktiku  řešit, řešit, řešit! V počátcích soutěže má každý tým k dispozici sedm úloh a vždy, když nějakou vyřeší, otevře se mu další. Zpočátku jsme postupovali poměrně rychle, růst obtížnosti úloh byl však znát. Velmi příjemným zpestřením bylo v 18:00 spuštění tzv. Hurry-up série, která obsahuje úlohy jednodušší, tudíž nás opět nakopla.<br>\r\n<br>\r\nPřes původní záměr, udělat v polovině soutěže krátký oddech, jsme od záměru upustili, řídíce se starou Cimrmanovou zásadou „Nedělat přestávku, jinak (nám týmy v pořadí) utečou!“ Bylo to sice kruté, ale muselo to být! Ale rozhodně se to vyplatilo. V rámci Česka a Slovenska jsme se v naší kategorii umístili na 8 místě. Při soutěži jsme sice byli zavaleni úlohami, užili jsme si však spoustu legrace a již se nemůžeme dočkat nejbližší podobné akce, kterou by mělo být 12. 2. 2021 Fyziklání, a rozhodně doporučujeme se přihlásit každému, koho fyzika baví a nechce o takový zážitek přijít.',	'cs'),
(45,	150,	'Knock knock<br>\r\nWho’s there?<br>\r\nBoo!<br>\r\nBoo who?<br>\r\nDon’t cry!<br>\r\n<br>\r\nZdravíme ptáka Fykosáka s fotkou, ktorá Vám rozžiari posledné dni spooky season! Ďakujeme, že sme sa mohli zúčastniť Fyziklání, ktorým ste nám spríjemnili sychravé jesenné dni. Naozaj sme sa zabavili!<br>\r\nS láskou, Mančaft ',	'cs'),
(46,	150,	'',	'en'),
(47,	150,	'',	'en'),
(48,	150,	'This is the first time any member of our team has ever been in the competition though all of us have participated in Physics Olympiads, national and international. Although we only finished 23rd overall but Physics Brawl was a fun experience: the problems were very challenging and the competition as a whole was very well organized. We learned some valuable lessons from the competition: teamwork, communication, planning, working efficiently and so much more. We certainly will return next year and hopefully with better results.<br>\r\n<br>\r\nThe team photo was taken at 2:22 AM in Vietnam, right after the competition. (The second lad from the left is our team mascot and the inspiration of our team’s name.)',	'en'),
(49,	150,	'This year, we participated in Physics Brawl for the first time. It was a very intense and fun competition and a welcome source of exhilaration to solve the intriguing questions presented by the organizers. Especially during the COVID-19 pandemic, it was great to be able to work in a team environment again. We definitely bonded as a team and improved our physics abilities. Thank you for organizing this great competition! We look forward to partaking in this competition again in future years!',	'en'),
(50,	150,	'',	'cs'),
(51,	150,	'We knew about Physics Brawl this year for the first time. So, it was our first participation. It was a great experience. Solving tough physics problems for hours was fun and helped us to open our minds, and it was great to work with a group and collaborate together. Because of the Coronavirus, we were not able to gather together in one place to solve the competition questions, but we bypassed this matter by having an online meeting to discuss the physics problems together. we placed first in our country in our group (group c)! This exciting competition increased our love for physics. It was an honest competition. we wish to thank the competition organizers for providing us this opportunity. And we hope that we can join this competition again.',	'en'),
(52,	150,	'Our team \\nabla taco (PhODS), contrary to many others teams in the Online Physics Brawl, originated from a Physics Olympiad discord server (PhODS). Because of this, our team was very diverse (members were from the US, Azerbaijan, and India) and our team members had a 12+ hour time zone difference. However, the Physics Brawl was placed at a convenient time such that everyone on the team could compete (which was awesome!). As the three hours went in the competition, we found the Physics Brawl to be a fascinating contest. Since there is a three hour time limit, there was an extensive amount of teamwork involved. There was a lot of communication between members and sometimes 2-3 people had to work together on the hardest problems and we also had to split up roles. We discussed together on a voice call which was very lively with everyone discussing different problems all at the same time. The fast, yet relaxing atmosphere, made the Physics Brawl to be a great experience. This event helped all of us to get to know each other much better than before the competition. For that reason, we really want to thank all the organizers and people who participated to make such a great experience! We definitely will be doing this competition next year and telling other people about it.',	'en'),
(53,	150,	'This was the first time that our team was participating in this competition. We definitely were looking forward to participating in this competition and to solve physics problems together as a team as well. The competition definitely tested how well we know our physics and improved our knowledge of it as well. We will certainly come back next year, because of how exciting and fun it is. We are looking forward to future editions of this competition as well now.',	'en'),
(54,	150,	'My name is Iva Pešić, I\'m 16. I am currently attending gymnasium and High ballet school, and I\'ve played in a lot of numeros, performances in National Theater in Belgrade and competitions. Also I am member of company ,,Balet mladih/ Youth ballet\" at Belgrade Dance Institute. I am a member of a project ,,Beotura\" supported by the Ministry of Education, Science and Technological Development of the Republic of Serbia, I also write scientific papers for the Institute of Genetics and Genetic Research in Belgrade (Serbia).<br>\r\n<br>\r\nI am responsible, I try to always give my best. I am creative, hardworking, persistent.I enjoy working with people. I like to guide, motivate and encourage them in developing their skills. I am also actively involved in humanitarian work (I have led and organized humanitarian concerts) and volunteering (EUG).<br>\r\n<br>\r\nAs a very young child, I discovered a world that I did not know existed until then. It was the world of physics. Formulas and scientific phenomena have become my play corner. I could hardly wait to get physics as a subject in school. Finally, my wishes came true, numerous awards, praises, recognitions...<br>\r\n<br>\r\nI was attracted to this competition by my love of physics, as well as my great desire for progress. The assignments were very interesting, however there were also assignments we had not learned yet. I am very glad that I participated in the realization of the largest online physics competition in the world.<br>\r\nI hope and look forward to further cooperation.',	'en'),
(55,	150,	'Hi, we are the members of Schrondingers Army and being one of the youngest competitors in the brawl, it was our first hand experience :)<br>\r\n <br>\r\nLet us introduce ourselves first.<br>\r\nRachit Ahuja, Sidharth Valsan, Shatadrumi Dey, Vishwas Middha and Mudil Goel.<br>\r\n <br>\r\nWe are all 14 year olds belonging to different parts of India. Divided by region, united by Interest.<br>\r\n <br>\r\nWe came to know about it through one of our physics teachers who encouraged us to participate in it and here we are!<br>\r\n <br>\r\nBelonging to India, where exposure to Analytical and practical physics is provided very less that too at a very basic level at our schools, we all are olympiad aspirants who want to do and learn something new everyday much out of our regular curriculum.<br>\r\nSo we thought it’d be really interesting to give a shot at it and test our skills!<br>\r\n <br>\r\nThough we couldn’t meet in person because of the pandemic we managed to gave a pretty good discussion online with each other.<br>\r\n <br>\r\nTo describe the questions as an one-liner, we’d say it was “a perfect amalgamation of creativity, logic, and theoretical knowledge”.<br>\r\n <br>\r\nThey were really appreciable and we loved the mode of conduct of the competition. <br>\r\n <br>\r\nNumerous teams from all parts of not only a country, but all throughout the world, competing for a position is an experience worth having.<br>\r\n <br>\r\nIn addition to solving problems, we spent a really good 3hrs interacting, exchanging our views and ideas on various questions and helping each others solving bits and pieces to complete the whole puzzle!<br>\r\n <br>\r\n“Learn to stand when you get to fall,<br>\r\nWinning isn’t everything, participation is all”<br>\r\n <br>\r\nSomehow we as a team, managed to bag the 6th position in our country and 57th position worldwide, it encourages us further to continue participating in the coming years and lets hope we might bring the 1st position for our country!<br>\r\n <br>\r\nOverall it was a very out-of-the-box experience...',	'en'),
(56,	150,	'We all know that electrons interact through the electromagnetic field and protons interact with neutrons through the strong nuclear force. But how do the so-called “noob particles” interact? In order to answer that question, we collided five of them together and observed their interaction over the course of three intense hours. <br>\r\n<br>\r\nThroughout this experiment, The Physics Brawl competition offered a wide range of “facilities” to help us carry out our experiment. From the Spherical Harmonics Expansion accelerator to the Tesseract Resistor trap, our team was able to perform many intricate experiments to elucidate the properties of the noob particles. There are even malicious traps in which they are burnt and breathed in aliens’ lungs to test their chemical and biological properties. It was entirely unexpected, but the particles resisted all such attempts.<br>\r\n<br>\r\nWe have found that the noob particles exhibit many interesting properties. With a well-planned and unrelenting demonstration of a mechanism called ‘teamwork’, the noob particles were able to accelerate to energy levels as high as “Problem 51”. The many experimental facilities that the Physics Brawl provided helped us find that the noob particles also have a property called “algebraic manipulation ability”. Over the 3-hour-timespan, we have found the value of such property increases tremendously.<br>\r\n<br>\r\nOur team truly appreciates how the Physics Brawl Organizers provided us with the means of obtaining such valuable data. We hope that we can continue our collaboration in the future to extend further our knowledge of the noob particles and physics in general.',	'en'),
(57,	150,	'I am glad to have been able to join physics brawl. In fact, the time when this contes was held was 1:00a.m~4:00a.m. in Japan. So I was really sleepy at that time. However, end of the contest, I was so excited that I didn’t feel sleepiness. Thank you for holding this event.',	'en'),
(58,	150,	'We\'re not the only team to participate from different regions in the same country, but if you know what the internet is like in Syria, you\'ll understand our struggle. Our team leader told us about the competition and we really wanted to participate despite the weak internet connection and the distances between us. We’re from three distant cities, about 360, 186, 255 kilometers away from each other respectively. Due to some technical issues and unfortunate accidents, everyone, except our leader and I (whom I live with in the same city and was able to meet with for the competition), had to participate from home and communicate through a Google Meet call. To make matters worse, it started raining which weakened the 4G network that we even couldn’t hear each other for almost half the time. Despite all my complaints, we absolutely loved the competition. The problems were creative and innovative, we were really challenged by them but yet we had so much fun! Looking forward to participating next year and we hope to get a better result!',	'en'),
(59,	150,	'',	'en'),
(60,	150,	'We participated this year for the first time in the Online Physics Brawl competition, and we can really say that it was much better than we expected, although we didn\'t achieve a position of pride or high marks, it is still one of the most enjoyable experiences we have had this year in light of this Conditions the country is going through.<br>\r\n<br>\r\nDuring the confrontation, we encountered many problems during our participation, such as differences between the team or our lack of knowledge of how to develop a good strategy, but we\'re now fully aware of our mistakes and how to fix them and we really hope that we can participate again in this competition, that the competition not only gives us knowledge but also gives us pleasure, teamwork, speed, strategy, and sportsmanship. Really, we are proud of all the teams that have achieved first positions and we are proud of them and we wish them all the best and also grateful to the FYKOS team, we support them with all our energy to continue.<br>\r\n<br>\r\nWith our regards,<br>\r\nFamily Jules Team',	'en'),
(61,	159,	'Thank you, sir, for such a beautiful and nice contest. It was total refreshment and gave confidence to my physics. And for the upcoming Jee exam and olympiads. ',	'en'),
(62,	159,	'Hi,\r\n My name is David Constantinescu and I want to thank you for the effort of organising an excellent Physics competition. This year, like last year too, I enjoyed solving innovative problems and the fact that this contest is a team competition makes him unique among Physics contests !\r\n\r\nThis year I participated in B category with OCRR team and we are on the 4th place.\r\n\r\n',	'en'),
(63,	159,	'Hello, I am Rajkumar Verma. I was at 36th position in category C in physics brawl competition. I like their style of test. It was very interesting. I enjoy the test. And I highly recommend  this type of competition.\r\nThanks.\r\n',	'en');
